        Answers.objects.create_answers(Context.objects.get(uuid='6b0353e2-b865-4676-b36c-ce9f13c0956b'),'''<p><strong>Please select an option. </strong></p>''',None,None)
        Tag.objects.create_tags(Context.objects.get(uuid='6b0353e2-b865-4676-b36c-ce9f13c0956b'), ['Issue of duplicate certificate', 'Loss of share certificate', 'loss of shares', 'loss of deb cert', 'issue of duplicate', 'lost', 'misplaced', 'market value exceeds', 'five lacs', 'five lakhs'])
