        Answers.objects.create_answers(Context.objects.get(uuid='1a5e476c-0323-4ac0-8120-a5de06d44ed5'),'''<p class="ql-align-justify"><strong style="color: rgb(32, 31, 30);">Service of Investor Request for Loss of Securities as per</strong><strong style="color: rgb(230, 0, 0);">&nbsp;[ Form ISR-4 SIMPLIFIED ] </strong><strong> </strong><span style="color: rgb(32, 31, 30);">duly filled in all respect as per SEBI Circular dated 25th January</span><sup style="color: rgb(32, 31, 30);">th</sup><span style="color: rgb(32, 31, 30);">January, 2022. SEBI has mandated the listed companies to issue securities in Demat mode only, pursuant to the investor service request.</span></p>''',Document.objects.get(document='Form_ISR-4_EWou10l.pdf'),None)
